.bootstrap-datetimepicker-widget.date-time-picker-pf .timepicker-minutes .table-condensed>tbody>tr>td {
  padding: 7px 0;
}

.classification-banner-pf-banner {
  background: #030303;
  background-clip: padding-box;
  color: #fff;
  height: 28px;
  line-height: 28px;
  font-size: 12px;
  position: relative;
}

.classification-banner-pf-banner-top {
  width: 100%;
  height: 31px;
}

.classification-banner-pf-banner-top::before {
  content: '';
  background: rgba(255,255,255,0.5);
  display: block;
  width: 100%;
  height: 3px;
}

.classification-banner-pf-banner-bottom {
  width: 100%;
}

.classification-banner-pf-green {
  background: #3f9c35;
}

.classification-banner-pf-red {
  background: #c00;
}

.classification-banner-pf-blue {
  background: #0088ce;
}

.classification-banner-pf-orange {
  background: #ec7a08;
}

.classification-banner-pf-yellow {
  background: #f0ab00;
}

.classification-banner-pf-banner-left {
  position: absolute;
  left: 42px;
  top: 50%;
  transform: translate(0, -50%);
  display: flex;
  flex-direction: row;
  justify-content: flex-start;
}

.classification-banner-pf-banner-left>span:nth-of-type(2) {
  border-left: 1px solid rgba(255,255,255,0.5);
  border-right: 1px solid rgba(255,255,255,0.5);
  padding: 0 20px;
}

.classification-banner-pf-banner-left>span:nth-of-type(1) {
  padding-right: 20px;
}

.classification-banner-pf-banner-right {
  position: absolute;
  right: 20px;
  top: 50%;
  transform: translate(0, -50%);
  display: flex;
  flex-direction: row-reverse;
  justify-content: flex-start;
}

.classification-banner-pf-banner-right>span:nth-of-type(2) {
  border-left: 1px solid rgba(255,255,255,0.5);
  border-right: 1px solid rgba(255,255,255,0.5);
  padding: 0 20px;
}

.classification-banner-pf-banner-right>span:nth-of-type(1) {
  padding-left: 20px;
}

.classification-banner-pf-close::before {
  color: #fff;
}

.classification-banner-pf-classification-level {
  text-transform: uppercase;
  letter-spacing: 2px;
  position: absolute;
  font-weight: bolder;
  font-size: 14px;
  left: 50%;
  top: 50%;
  transform: translate(-50%, -50%);
}

.classification-banner-pf-children {
  height: calc(100vh - 59px);
}

.classification-banner-pf-children-no-bottom {
  height: calc(100vh - 31px);
}

@media only screen and (max-width: 415px) {
  .classification-banner-pf-banner-left {
    visibility: hidden;
  }

  .classification-banner-pf-banner-right {
    visibility: hidden;
  }
}

.access-consoles-pf .desktop-viewer-pf {
  margin-top: 3em;
}

.console-selector-pf {
  margin-bottom: 0;
  float: left;
}

.console-selector-pf-disconnect-switch {
  margin-left: 15px;
}

.serial-console-pf {
  height: 100%;
}

.serial-console-pf .toolbar-pf-results {
  padding: 15px 0;
  border-top: none;
  margin-top: 0;
}

.serial-console-pf .console-pf {
  font-family: Menlo,Monaco,Consolas,"Courier New",monospace;
  font-size: 15px;
  margin-top: 0;
  margin-bottom: 0;
  text-align: center;
  line-height: normal;
  height: 100%;
}

.serial-console-pf .console-pf .terminal {
  color: #fff;
  text-align: left;
  outline: medium none;
  background-color: #030303;
  border: 1px solid #030303;
  padding: 10px;
}

.serial-console-pf .console-pf .terminal .terminal-cursor {
  border: 1px solid #fff;
}

.serial-console-pf .console-pf .terminal:focus .terminal-cursor {
  border: none;
  animation: blink 1s step-end infinite;
}

.serial-console-pf .console-terminal-pf {
  width: 100%;
  height: 100%;
  padding-right: 0;
  padding-left: 0;
}

.serial-console-pf .console-actions-buttons-pf {
  margin-left: 5px;
}

@keyframes blink {
  from {
    color: #030303;
    background: #fff;
  }

  50% {
    color: #fff;
    background: #030303;
  }
}

.vnc-console .toolbar-pf-results {
  padding: 15px 0;
  border-top: none;
  margin-top: 15px;
}

.vnc-console .vnc-console-connecting {
  background-color: #3f9c35;
}

.vnc-console .vnc-console-disconnected {
  background-color: #c00;
}

.vnc-console .toolbar-pf-action-right {
  padding-bottom: 5px;
}

.vnc-console .toolbar-pf-action-right .dropdown-menu {
  min-width: 102px;
}

.desktop-viewer-pf {
  display: flex;
  flex-wrap: wrap;
}

.desktop-viewer-pf .manual-connection-pf {
  min-width: 350px;
}

.desktop-viewer-pf .manual-connection-pf>h2 {
  text-align: center;
}

.desktop-viewer-pf .manual-connection-pf>p {
  text-align: center;
}

.desktop-viewer-pf .manual-connection-pf .form {
  min-width: 350px;
}

.desktop-viewer-pf .manual-connection-pf .form-group {
  margin-bottom: 0;
}

.desktop-viewer-pf .manual-connection-pf .manual-connection-pf-title {
  text-align: right;
}

.desktop-viewer-pf .manual-connection-pf .manual-connection-pf-value {
  text-align: left;
  font-weight: bolder;
}

.desktop-viewer-pf .remote-viewer-pf {
  position: relative;
  top: 15%;
  width: 50%;
  min-width: 350px;
  margin-right: 50px;
}

.desktop-viewer-pf .remote-viewer-pf>h2 {
  text-align: center;
}

.desktop-viewer-pf .remote-viewer-pf .expand-collapse-pf {
  margin-top: 1em;
}

.desktop-viewer-pf .remote-viewer-pf .remote-viewer-pf-launch {
  text-align: center;
  margin-top: 20px;
}

.desktop-viewer-pf .remote-viewer-pf .remote-viewer-pf-launch>button {
  margin-left: 1em;
}

.desktop-viewer-pf .remote-viewer-pf .more-information-pf-install {
  display: inline-flex;
}

.desktop-viewer-pf .remote-viewer-pf .more-information-pf-install>b {
  margin-right: 5px;
}

