// Patternfly Mixins
// --------------------------------------------------
// PatternFly-specific mixins

// Patternfly button variants.
@mixin button-variant($color, $background, $background-image-start, $background-image-stop, $border){
  background-color: $background;
  @include gradient-vertical($background-image-start, $background-image-stop);
  border-color: $border;
  color: $color;

  &:hover,
  &:focus,
  &:active,
  &.active,
  .open .dropdown-toggle#{&} {
    background-color: $background;
    background-image: none;
    border-color: $border;
    color: $color;
  }
  &:active,
  &.active,
  .open .dropdown-toggle#{&} {
    background-image: none;
    &:hover,
    &:focus,
    &.focus {
      background-color: darken($background, 5%);
          border-color: darken($border, 7%);
    }
  }
  &.disabled,
  &[disabled],
  fieldset[disabled] & {
    &,
    &:hover,
    &:focus,
    &:active,
    &.active {
      background-color: $background;
      border-color: $border;
    }
  }
}

// SASS CONVERSION FIX.
// Allows inclusion of sr-only rules in nested media queries where @extends .sr-only
// will not work.
@mixin sr-only-pf(){
  position: absolute;
  width: 1px;
  height: 1px;
  margin: -1px;
  padding: 0;
  overflow: hidden;
  clip: rect(0, 0, 0, 0);
  border: 0;
}

@mixin tab-indicator($background: $gray-light-pf, $left: 15px, $right: 15px){
  &:before {
    background: $background;
    bottom: -1px;
    content: "";
    display: block;
    height: 2px;
    left: $left;
    position: absolute;
    right: $right;
  }
}

// Ellipse overflow text
.text-overflow-pf {
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
  word-wrap: normal;
}
","@import url(~xterm/dist/xterm.css);

.text-overflow-pf {
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
  word-wrap: normal;
}

.card-pf-match-height[style*='height'] .card-pf-footer {
  position: absolute;
  bottom: 0;
  width: 100%;
}

.cards-pf .blank-slate-pf {
  background: none;
  border: none;
  padding: 30px;
}

.cards-pf .blank-slate-pf .blank-slate-pf-icon {
  color: #d1d1d1;
}

.bullet-chart-pf-legend-item-box.error {
  background-color: #ebccd1;
}

.bullet-chart-pf-legend-item-box.warning {
  background-color: #faebcc;
}

.utilization-bar-pf .display-inline-block {
  display: inline-block;
  font-size: 11px;
}

.utilization-bar-pf .label-text.progress-description {
  color: #030303;
  font-size: 12px;
}

.breadcrumbs-pf-title .active span {
  font-size: 16px;
  vertical-align: -2px;
}

.label a.pf-remove-button {
  color: #fff;
  display: inline-block;
  margin-left: 5px;
}

.modal.right-side-modal-pf {
  left: initial;
}

.modal.right-side-modal-pf .modal-dialog {
  margin: 60px 0 0 10px;
}

.modal.right-side-modal-pf .modal-dialog .modal-content {
  height: calc(100vh - 60px);
  display: flex;
  flex-direction: column;
}

.modal.right-side-modal-pf .modal-dialog .modal-content .modal-body {
  flex-grow: 1;
  overflow: hidden auto;
}

.modal.right-side-modal-pf.fade:not(.in) .modal-dialog {
  -webkit-transform: translate3d(-25%, 0, 0);
  transform: translate3d(25%, 0, 0);
}

.modal.modeless-pf.shown {
  display: block;
}

.drawer-pf.drawer-alt-pf {
  overflow-y: hidden;
  display: flex;
  flex-direction: column;
}

.drawer-pf.drawer-alt-pf .drawer-pf-title {
  position: relative;
}

.drawer-pf.drawer-alt-pf .panel-group {
  display: flex;
  flex-direction: column;
  position: initial;
  bottom: initial;
  top: initial;
  overflow-y: auto;
}

.drawer-pf.drawer-alt-pf .panel-group .panel.panel-default.expanded {
  min-height: 175px;
  flex: 1 1 auto;
  display: flex;
  flex-direction: column;
}

.drawer-pf.drawer-alt-pf .panel-group .panel-collapse.in {
  display: flex;
  flex-direction: column;
  flex: 1 1 auto;
  min-height: 0;
}

.drawer-pf.drawer-alt-pf .panel-group .panel-collapse.in .panel-body {
  flex: 1 1 auto;
  overflow-y: auto;
}

.drawer-pf.drawer-alt-pf .drawer-pf-action {
  flex: none;
}

table.pf-table-inline-edit thead tr.editing th {
  border-bottom-color: #7dc3e8;
  border-bottom-width: 3px;
}

table.pf-table-inline-edit tbody tr td {
  padding: 5px 10px 3px;
}

table.pf-table-inline-edit tbody tr td.editable {
  padding: 0;
}

table.pf-table-inline-edit tbody tr td.editable.editing {
  padding: 2px 3px;
}

table.pf-table-inline-edit tbody tr.editing td {
  background: #f5f5f5;
  border-top-color: #7dc3e8;
  border-bottom-color: #7dc3e8;
}

table.pf-table-inline-edit tbody tr.editing td:first-child {
  border-left-color: #7dc3e8;
}

table.pf-table-inline-edit tbody tr.editing td:last-child {
  border-right-color: #7dc3e8;
}

table.pf-table-inline-edit tbody .input {
  border: 1px solid transparent;
  display: block;
  margin: 2px 3px 2px;
  min-height: 26px;
  padding: 3px 6px 1px;
}

table.pf-table-inline-edit tbody .input:hover {
  background: #fff;
  border: 1px solid #ccc;
  cursor: text;
}

table.pf-table-inline-edit tbody .input:hover::after {
  content: "\f040";
  float: right;
  font-family: "FontAwesome";
  margin-left: 15px;
}

table.pf-table-inline-edit tbody .static {
  border: 1px solid transparent;
  display: block;
  margin: 2px 3px 2px;
  padding: 3px 6px 1px;
}

.inline-edit-buttons {
  background: #f5f5f5;
  border: 1px solid #7dc3e8;
  margin: 0;
  padding: 4px;
  position: fixed;
  z-index: 1000;
}

.inline-edit-buttons.top {
  border-bottom: 0;
}

.inline-edit-buttons.bottom {
  border-top: 0;
}

.inline-edit-buttons.bold {
  border-width: 3px;
}

.inline-edit-buttons .btn {
  height: 26px;
  margin-left: 4px;
  width: 26px;
}

.inline-edit-buttons .btn:first-child {
  margin-left: 0;
}

.popover-pf-info {
  margin-left: 3px;
  padding: 0;
}

.rbt {
  outline: none;
}

.rbt-menu {
  margin-bottom: 2px;
}

.rbt-menu>li a {
  overflow: hidden;
  text-overflow: ellipsis;
}

.rbt-menu>li a:focus {
  outline: none;
}

.rbt-menu-pagination-option {
  text-align: center;
}

.rbt .rbt-input-main::-ms-clear {
  display: none;
}

.rbt-input-multi {
  cursor: text;
  overflow: hidden;
  position: relative;
  height: auto;
}

.rbt-input-multi.focus {
  -webkit-box-shadow: inset 0 1px 1px rgba(0,0,0,0.075),0 0 8px rgba(102,175,233,0.6);
  -moz-box-shadow: inset 0 1px 1px rgba(0,0,0,0.075),0 0 8px rgba(102,175,233,0.6);
  box-shadow: inset 0 1px 1px rgba(0,0,0,0.075),0 0 8px rgba(102,175,233,0.6);
  border-color: #66afe9;
  outline: 0;
}

.rbt-input-multi.form-control[disabled] {
  background-color: #e9ecef;
  opacity: 1;
}

.rbt-input-multi input::-moz-placeholder {
  color: #999;
  opacity: 1;
}

.rbt-input-multi input:-ms-input-placeholder {
  color: #999;
}

.rbt-input-multi input::-webkit-input-placeholder {
  color: #999;
}

.rbt-input-multi .rbt-input-wrapper {
  margin-bottom: -4px;
  margin-top: -1px;
  overflow: hidden;
}

.rbt-input-multi .rbt-input-main {
  height: 20px;
  margin: 1px 0 4px;
}

.rbt-input-multi .rbt-input-hint-container {
  display: inline-block;
}

.rbt-input-multi.input-lg .rbt-input-main,
.rbt-input-multi.form-control-lg .rbt-input-main {
  height: 24px;
}

.rbt-input-multi.input-sm .rbt-input-main,
.rbt-input-multi.form-control-sm .rbt-input-main {
  height: 18px;
}

.rbt-close {
  z-index: 1;
}

.rbt-close-lg {
  font-size: 24px;
}

.rbt-token {
  background-color: #e7f4ff;
  border: 0;
  border-radius: 2px;
  color: #1f8dd6;
  display: inline-block;
  line-height: 1em;
  margin: 0 3px 3px 0;
  padding: 4px 7px;
  position: relative;
}

.rbt-token-disabled {
  background-color: #ddd;
  color: #888;
  pointer-events: none;
}

.rbt-token-removeable {
  cursor: pointer;
  padding-right: 21px;
}

.rbt-token-active {
  background-color: #1f8dd6;
  color: #fff;
  outline: none;
  text-decoration: none;
}

.rbt-token .rbt-token-remove-button {
  bottom: 0;
  color: inherit;
  font-size: inherit;
  font-weight: normal;
  opacity: 1;
  outline: none;
  padding: 3px 7px;
  position: absolute;
  right: 0;
  text-shadow: none;
  top: -2px;
}

.rbt-loader {
  -webkit-animation: loader-animation 600ms infinite linear;
  -o-animation: loader-animation 600ms infinite linear;
  animation: loader-animation 600ms infinite linear;
  border: 1px solid #d5d5d5;
  border-radius: 50%;
  border-top-color: #1f8dd6;
  display: block;
  height: 16px;
  width: 16px;
}

.rbt-loader-lg {
  height: 20px;
